import React, { Component } from 'react'

export default class Item extends Component {
    
    state={
        mouse:false
    }
    handleMouse=(flag)=>{
       return ()=>{
         this.setState({
             mouse:flag
         })
       }
    }

    hanldeChange=(id)=>{
      return (event)=>{
        console.log(id,event.target.checked);
      }
    }

    render() {
        const {name} = this.props
        return (
        <li style={{backgroundColor:this.state.mouse ? '#ddd':'white'}} onMouseEnter={this.handleMouse(true)} onMouseLeave={this.handleMouse(false)}>
            <label>
                <input type="checkbox" defaultChecked={this.props.done} onChange={this.hanldeChange(this.props.id)}></input>
                <span>{name}</span>
            </label>
            <button className="btn btn-danger" style={{display:'none'}}>删除</button>
        </li>
        )
    }
}
